package cmd

import (
	"regexp"
	"strings"
	"testing"
)

func TestMergeWithEncryptedTarget(t *testing.T) {
	if _, err := Merge(
		[]byte(`kind: Secret
data:
  ANOTHER_KEY: 1YCDl2ru+xjkRdSNQzJhYLssvaE=.qYp+dWMiZzSZ0q12.O8u3oaDqJIrMt9MXFKK8eA==
  KEY: AgsWlPl4J9U=.5pxxHJKYI/hnLkOe.swJPGDWdYL3ygKJQJJSpdg==
# kubesec:v:1
# kubesec:pgp:160A7A9CF46221A56B06AD64461A804F2609FD89:
`),
		[]byte(`kind: Secret
data:
  YET_ANOTHER_KEY: wJ5nNssGFZHvCfabBAElcVTSHfkfPXSl.o2J2BXdkfoW+45Am.umGbbu1Hc5wfGUt2OhIOkQ==
  KEY: trb+MUYyWaw=.fkR7QgUBLp/y943i.8+/1ty5wFo3tLo3QLETiLA==
# kubesec:v:1
# kubesec:pgp:160A7A9CF46221A56B06AD64461A804F2609FD89:
`),
	); err == nil {
		t.Fail()
	} else {
		actual := err.Error()
		expected := "[target] must not be encrypted"
		if actual != expected {
			t.Fatalf("actual: %#v != expected: %#v", actual, expected)
		}
	}
}

func TestMergeSubset(t *testing.T) {
	actual, err := Merge(
		[]byte(`kind: Secret
data:
  ANOTHER_KEY: 1YCDl2ru+xjkRdSNQzJhYLssvaE=.qYp+dWMiZzSZ0q12.O8u3oaDqJIrMt9MXFKK8eA==
  KEY: AgsWlPl4J9U=.5pxxHJKYI/hnLkOe.swJPGDWdYL3ygKJQJJSpdg==
# kubesec:v:1
# kubesec:pgp:160A7A9CF46221A56B06AD64461A804F2609FD89:
`),
		[]byte(`kind: Secret
data:
  KEY: DEFAULT_VALUE
`),
	)
	if err != nil {
		t.Fatal(err)
	}
	expected := `data:
  KEY: AgsWlPl4J9U=.5pxxHJKYI/hnLkOe.swJPGDWdYL3ygKJQJJSpdg==
kind: Secret
# kubesec:v:1
# kubesec:pgp:160A7A9CF46221A56B06AD64461A804F2609FD89:
`
	if string(actual) != expected {
		t.Fatalf("actual: %#v != expected: %#v", string(actual), expected)
	}
}

func TestMergeWithUnencryptedSource(t *testing.T) {
	if _, err := Merge(
		[]byte(`kind: Secret
data:
  ANOTHER_KEY: ANOTHER_VALUE
  KEY: VALUE
`),
		[]byte(`kind: Secret
data:
  KEY: DEFAULT_VALUE
`),
	); err == nil {
		t.Fail()
	} else {
		actual := err.Error()
		expected := "[source] must be encrypted"
		if actual != expected {
			t.Fatalf("actual: %#v != expected: %#v", actual, expected)
		}
	}
}

func TestMerge(t *testing.T) {
	encrypted, err := Encrypt([]byte(`{"kind":"Secret","data":{"KEY":"VALUE","ANOTHER_KEY":"ANOTHER_VALUE"}}`),
		EncryptionContext{})
	if err != nil {
		t.Fatal(err)
	}
	encryptedRS, err := unmarshal(encrypted)
	if err != nil {
		t.Fatal(err)
	}
	encryptedValue := encryptedRS.data()["KEY"]
	actual, err := Merge(
		[]byte(encrypted),
		[]byte(`kind: Secret
data:
  YET_ANOTHER_KEY: DEFAULT_YET_ANOTHER_VALUE
  KEY: DEFAULT_VALUE
`),
	)
	if err != nil {
		t.Fatal(err)
	}
	expected := `data:
  KEY: ` + encryptedValue + `
  YET_ANOTHER_KEY: ANYTHING
kind: Secret
# kubesec:v:1
# kubesec:pgp:ANYTHING`
	if !regexp.MustCompile(strings.Replace(regexp.QuoteMeta(expected), "ANYTHING", "[^\\n]*", -1)).
		MatchString(string(actual)) {
		t.Fatalf("actual: %#v != expected: %#v", string(actual), expected)
	}
}
